-- Create tables
DROP TABLE A_PEOPLE;
CREATE TABLE A_PEOPLE 
(
  ID SERIAL, 
  LASTNAME VARCHAR(50), 
  FIRSTNAME VARCHAR(50), 
  CREATEDAT Timestamp(6) WITH TIME ZONE NOT NULL DEFAULT CURRENT_TIMESTAMP,
  UPDATEDAT Timestamp(6) WITH TIME ZONE NOT NULL DEFAULT CURRENT_TIMESTAMP,
  UPDATEDBY VARCHAR(50 BYTE) DEFAULT 'NO_ID',
  CONSTRAINT A_PEOPLE_PK PRIMARY KEY (ID) 
);

--DROP TABLE A_CATS;
CREATE TABLE A_CATS 
(
  PEOPLE_ID DOUBLE PRECISION NOT NULL, 
  ID DOUBLE PRECISION NOT NULL, 
  NAME VARCHAR(50), 
  TITLE VARCHAR(50), 
  AGE DOUBLE PRECISION, 
  SKILL VARCHAR(50), 
  CREATEDAT Timestamp(6) WITH TIME ZONE NOT NULL DEFAULT CURRENT_TIMESTAMP,
  UPDATEDAT Timestamp(6) WITH TIME ZONE NOT NULL DEFAULT CURRENT_TIMESTAMP,
  UPDATEDBY VARCHAR(50 BYTE) DEFAULT 'NO_ID',
  CONSTRAINT A_CATS_PK PRIMARY KEY (PEOPLE_ID,ID) 
);

--DROP TABLE A_DOGS;
CREATE TABLE A_DOGS 
(
  PEOPLE_ID DOUBLE PRECISION NOT NULL, 
  ID DOUBLE PRECISION NOT NULL, 
  NAME VARCHAR(50), 
  TITLE VARCHAR(50), 
  AGE DOUBLE PRECISION, 
  SKILL VARCHAR(50), 
  CREATEDAT Timestamp(6) WITH TIME ZONE NOT NULL DEFAULT CURRENT_TIMESTAMP,
  UPDATEDAT Timestamp(6) WITH TIME ZONE NOT NULL DEFAULT CURRENT_TIMESTAMP,
  UPDATEDBY VARCHAR(50 BYTE) DEFAULT 'NO_ID',
  CONSTRAINT A_DOGS_PK PRIMARY KEY (PEOPLE_ID,ID) 
);

--DROP TABLE A_BIRDS;
CREATE TABLE A_BIRDS 
(
  PEOPLE_ID DOUBLE PRECISION NOT NULL, 
  ID DOUBLE PRECISION NOT NULL, 
